#include &lt;iostream&gt;
using namespace std;

// defining the default arguments
void display(char c = &#39;*&#39;, int count = 3) {
    for(int i = 1; i &lt;= count; ++i) {
        cout &lt;&lt; c;
    }
    cout &lt;&lt; endl;
}

int main() {
    int count = 5;

    cout &lt;&lt; &quot;No argument passed: &quot;;
    // *, 3 will be parameters
    display(); 

    cout &lt;&lt; &quot;First argument passed: &quot;;
     // #, 3 will be parameters
    display(&#39;#&#39;); 

    cout &lt;&lt; &quot;Both argument passed: &quot;;
    // $, 5 will be parameters
    display(&#39;$&#39;, count); 

    return 0;
}

